mapper.select:
SELECT COUNT(id) as cnt
FROM mem
WHERE id=#{id}

SELECT memno, id, passwd, mname, tel, zipcode, address1, address2, mdate, grade
FROM mem
WHERE memno = #{memno}

SELECT memno, id, passwd, mname, tel, zipcode, address1, address2, mdate, grade
FROM mem
WHERE id = #{id}

mapper.update:
UPDATE mem
SET id=#{id}, mname=#{mname}, tel=#{tel}, zipcode=#{zipcode}, address1=#{address1}, address2=#{address2}
WHERE memno=#{memno}

mapper.select:
SELECT COUNT(memno) as cnt
FROM mem
WHERE id=#{id} AND passwd=#{passwd}

SELECT COUNT(memno) as cnt
FROM mem
WHERE memno=#{memno} AND passwd=#{passwd}

mapper.update:
UPDATE mem
SET passwd=#{passwd}
WHERE memno=#{memno}

mapper.select:
SELECT COUNT(*) as cnt
FROM mem 
WHERE id={#id} AND grade >= 40 AND grade <= 49;
